import React, { useState, useEffect, useRef } from 'react';

function EyeTracking() {
  const [tracking, setTracking] = useState(false);
  const [coords, setCoords] = useState({ x: null, y: null });
  const [focusLost, setFocusLost] = useState(false);
  const intervalRef = useRef(null);
  const lastSeenRef = useRef(Date.now());

  const startTracking = async () => {
    try {
      if (window.webgazer) {
        await window.webgazer.setRegression('ridge')
          .setGazeListener((data) => {
            if (data) {
              setCoords({ x: Math.round(data.x), y: Math.round(data.y) });
              lastSeenRef.current = Date.now();
              setFocusLost(false);
            }
          });

        await window.webgazer.begin();
        window.webgazer.showVideoPreview(true);
        window.webgazer.showPredictionPoints(true);
        window.webgazer.showFaceOverlay(true);
        setTracking(true);

        intervalRef.current = setInterval(() => {
          if (Date.now() - lastSeenRef.current > 4000) {
            setFocusLost(true);
          }
        }, 1000);
      }
    } catch (err) {
      console.error('WebGazer start failed:', err);
    }
  };

 const stopTracking = () => {
  // Stop intervals
  clearInterval(intervalRef.current);

  // Remove overlays FIRST, safely
  ['webgazerVideoFeed', 'webgazerFaceOverlay', 'webgazerFaceFeedback'].forEach(id => {
    const el = document.getElementById(id);
    if (el && el.remove) {
      el.remove();
    }
  });

  // Stop WebGazer
  if (window.webgazer) {
    try {
      window.webgazer.clearGazeListener();
      window.webgazer.end(); // this line sometimes throws if DOM elements already removed
    } catch (error) {
      console.warn("webgazer.end() error:", error.message);
    }
  }

  // Stop camera if still active
  const video = document.querySelector('video');
  if (video?.srcObject) {
    video.srcObject.getTracks().forEach(track => track.stop());
  }

  setCoords({ x: null, y: null });
  setFocusLost(false);
  setTracking(false);
};


  useEffect(() => {
    return () => stopTracking(); // Cleanup when component unmounts
  }, []);

  return (
    <div style={{
      border: "2px dashed #334155",
      padding: "1rem",
      marginTop: "1rem",
      color: "white",
      backgroundColor: "#1e293b",
      borderRadius: "8px"
    }}>
      <h3>üëÅÔ∏è Eye Tracking & Focus Detection</h3>
      <p>Click start to begin webcam-based attention tracking.</p>

      <div style={{ marginTop: '1rem' }}>
        {!tracking ? (
          <button
            onClick={startTracking}
            style={{
              padding: "8px 16px",
              backgroundColor: "#22c55e",
              color: "white",
              border: "none",
              borderRadius: "6px",
              fontWeight: "bold",
              cursor: "pointer"
            }}
          >
            ‚ñ∂Ô∏è Start Eye Tracking
          </button>
        ) : (
          <button
            onClick={stopTracking}
            style={{
              padding: "8px 16px",
              backgroundColor: "#ef4444",
              color: "white",
              border: "none",
              borderRadius: "6px",
              fontWeight: "bold",
              cursor: "pointer"
            }}
          >
            ‚èπÔ∏è Stop Eye Tracking
          </button>
        )}
      </div>

      {tracking && (
        <div style={{
          border: "1px solid #475569",
          padding: "1rem",
          marginTop: "1rem",
          backgroundColor: "#0f172a",
          borderRadius: "6px"
        }}>
          <p><strong>Tracking your gaze:</strong></p>
          <p>X: {coords.x ?? '--'}, Y: {coords.y ?? '--'}</p>
          {focusLost && (
            <p style={{ color: "#f87171", fontWeight: "bold" }}>
              ‚ö†Ô∏è Focus Lost!
            </p>
          )}
        </div>
      )}
    </div>
  );
}

export default EyeTracking;
