import React, { useState, useEffect, useRef } from 'react';

function EyeTracking() {
  const [tracking, setTracking] = useState(false);
  const [coords, setCoords] = useState({ x: null, y: null });
  const [focusLost, setFocusLost] = useState(false);
  const intervalRef = useRef(null);
  const lastSeenRef = useRef(Date.now());

  const startTracking = async () => {
    try {
      if (window.webgazer) {
        await window.webgazer.setRegression('ridge')
          .setGazeListener((data) => {
            if (data) {
              setCoords({ x: Math.round(data.x), y: Math.round(data.y) });
              lastSeenRef.current = Date.now();
              setFocusLost(false);
            }
          });

        await window.webgazer.begin();
        window.webgazer.showVideoPreview(true);
        window.webgazer.showPredictionPoints(true);
        window.webgazer.showFaceOverlay(true);
        setTracking(true);

        intervalRef.current = setInterval(() => {
          if (Date.now() - lastSeenRef.current > 4000) {
            setFocusLost(true);
          }
        }, 1000);
      }
    } catch (err) {
      console.error('WebGazer start failed:', err);
    }
  };

  const stopTracking = () => {
    if (window.webgazer) {
      window.webgazer.pause();
      window.webgazer.end();

      // Remove leftover overlays
      ['webgazerVideoFeed', 'webgazerFaceOverlay', 'webgazerFaceFeedback'].forEach(id => {
        const el = document.getElementById(id);
        if (el) el.remove();
      });

      // Stop webcam stream if active
      const video = document.querySelector('video');
      if (video && video.srcObject) {
        video.srcObject.getTracks().forEach(track => track.stop());
      }
    }

    clearInterval(intervalRef.current);
    setCoords({ x: null, y: null });
    setFocusLost(false);
    setTracking(false);
  };

  useEffect(() => {
    return () => {
      stopTracking(); // Cleanup on component unmount
    };
  }, []);

  return (
    <div style={{ border: "2px dashed #ccc", padding: "1rem", marginTop: "1rem", color: "white" }}>
      <h3>üëÅÔ∏è Eye Tracking & Focus Detection</h3>
      <p>Click start to begin webcam-based attention tracking.</p>

      <div style={{ marginTop: '1rem' }}>
        {!tracking ? (
          <button
            onClick={startTracking}
            style={{
              padding: "8px 16px",
              backgroundColor: "#28a745",
              color: "white",
              border: "none",
              borderRadius: "5px",
              cursor: "pointer"
            }}
          >
            ‚ñ∂Ô∏è Start Eye Tracking
          </button>
        ) : (
          <button
            onClick={stopTracking}
            style={{
              padding: "8px 16px",
              backgroundColor: "#dc3545",
              color: "white",
              border: "none",
              borderRadius: "5px",
              cursor: "pointer"
            }}
          >
            ‚èπÔ∏è Stop Eye Tracking
          </button>
        )}
      </div>

      {tracking && (
        <div style={{ border: "1px solid #ddd", padding: "1rem", marginTop: "1rem" }}>
          <p><strong>Tracking your gaze here:</strong></p>
          <p>X: {coords.x ?? '--'}, Y: {coords.y ?? '--'}</p>
          {focusLost && (
            <p style={{ color: "red", fontWeight: "bold" }}>
              ‚ö†Ô∏è Focus Lost!
            </p>
          )}
        </div>
      )}
    </div>
  );
}

export default EyeTracking;
